DROP TRIGGER IF EXISTS `cart_product_trigger`;
delimiter ;;
CREATE TRIGGER `cart_product_trigger` BEFORE UPDATE ON `apm_store_cart` FOR EACH ROW BEGIN
 IF NEW.is_pay = 1 THEN
 UPDATE apm_store_product_attr_value SET stock = stock - NEW.cart_num WHERE product_id = NEW.product_id AND suk = NEW.product_attr_unique;
 UPDATE apm_store_product SET stock = stock - NEW.cart_num WHERE product_id = NEW.product_id;
 END IF;
END
;;
delimiter ;

