-- Triggers structure for table apm_store_product_attr_value
-- ----------------------------
DROP TRIGGER IF EXISTS `attr_value_product_add_trigger`;
delimiter ;;
CREATE TRIGGER `attr_value_product_add_trigger` AFTER INSERT ON `apm_store_product_attr_value` FOR EACH ROW BEGIN
 DECLARE newStock INT;
 SET newStock = (SELECT SUM(v.stock) FROM apm_store_product p, apm_store_product_attr_value v WHERE p.id = v.product_id AND v.product_id = NEW.product_id);
 UPDATE apm_store_product SET stock = newStock WHERE id = NEW.product_id;
END
;;
delimiter ;

