delimiter ;;
CREATE TRIGGER `attr_value_product_update_trigger` BEFORE UPDATE ON `apm_store_product_attr_value` FOR EACH ROW BEGIN
 DECLARE newStock INT;
 DECLARE newSales INT;
 SET newStock = (SELECT SUM(v.stock) FROM apm_store_product p, apm_store_product_attr_value v WHERE p.id = v.product_id AND v.product_id = NEW.product_id);
 SET newSales = (SELECT SUM(v.sales) FROM apm_store_product p, apm_store_product_attr_value v WHERE p.id = v.product_id AND v.product_id = NEW.product_id);
 UPDATE apm_store_product SET stock = newStock, sales = newSales WHERE id = NEW.product_id;
END
;;
delimiter ;

